import { makeStyles } from '@material-ui/core/styles';

export default makeStyles(() => ({
  media: {
    height: 260,
  },
  cardContent: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  cartActions: {
    justifyContent: 'space-between',
    // alignSelf: 'flex-end',
  },
  buttons: {
    display: 'flex',
    alignItems: 'center',
  },
  removeButton: {
    backgroundColor: '#c2c2c2',
  }
}));
